mapper.select:
select t.updatetime,t.updateperson,t.createtime,t.createperson,t.remark,t.isholiday,t.holidaytype,t.holidaydate,t.holidayname,t.year,t.id from T_BASE_HOLIDAYS t
where t.year = #{year}
and t.holidayname = #{holidayname}
order by t.year desc,t.holidaydate asc

mapper.update:
update T_BASE_HOLIDAYS
set 
	updatetime = #{updatetime,jdbcType=TIME}, updateperson = #{updateperson,jdbcType=VARCHAR}, createtime = #{createtime,jdbcType=TIME}, createperson = #{createperson,jdbcType=VARCHAR}, remark = #{remark,jdbcType=VARCHAR}, isholiday = #{isholiday,jdbcType=INTEGER}, holidaytype = #{holidaytype,jdbcType=VARCHAR}, holidaydate = #{holidaydate,jdbcType=TIME}, holidayname = #{holidayname,jdbcType=VARCHAR}, year = #{year,jdbcType=INTEGER}
where
	id = #{id}
